mapper.select:

SELECT
   ID,
   NAME,
   PHONE_NO
FROM  TB_MEMBER
WHERE HOME_ID = #{home_id}
  AND ID = #{id}
  AND PASSWORD = #{password}


SELECT
   PASSWORD
FROM  TB_MEMBER
WHERE HOME_ID = #{home_id}
  AND ID = #{id}
  AND NAME = #{name}
  AND PHONE_NO = #{phone_no}


SELECT
   ID
FROM  TB_MEMBER
WHERE HOME_ID = #{home_id}
  AND PASSWORD = #{password}
  AND NAME = #{name}
  AND PHONE_NO = #{phone_no}
  AND ROWNUM = 1
